import moment from 'moment'

export const twentySecondApril = '2000-04-22'
export const seventeenthDecember = '2021-12-17'
export const twentySecondAprilMoment = moment.utc(
	twentySecondApril,
	'YYYY-MM-DD'
)
export const seventeenthDecemberMoment = moment.utc(
	seventeenthDecember,
	'YYYY-MM-DD'
)
